  private initializeSampleAnnouncements() {
    const sampleAnnouncements: Announcement[] = [
      {
        id: "ann-1",
        eventId: "event-1",
        hostId: "sample-user-1",
        subject: "Workshop Materials Available",
        message: "All workshop materials and setup instructions have been posted. Please check your email for the downloadable resources.",
        createdAt: new Date()
      },
      {
        id: "ann-2",
        eventId: "event-1", 
        hostId: "sample-user-1",
        subject: "Parking Information Updated",
        message: "Free parking is available in the Tech Hub lot. Please enter through the north entrance and use your event confirmation for validation.",
        createdAt: new Date()
      },
      {
        id: "ann-3",
        eventId: "event-2",
        hostId: "sample-user-1", 
        subject: "Special Guest Artist",
        message: "We are excited to announce a special guest performance by Maria Santos, renowned local sculptor, at 3 PM.",
        createdAt: new Date()
      }
    ];

    sampleAnnouncements.forEach(announcement => {
      this.announcements.set(announcement.id, announcement);
    });

    // Initialize sample notifications
    const sampleNotifications: Notification[] = [
      {
        id: "notif-1",
        userId: "sample-user-1",
        type: "event_reminder",
        title: "Event Reminder: Web Development Bootcamp Tomorrow",
        message: "Don't forget about the Web Development Bootcamp starting tomorrow at 9:00 AM. See you there!",
        eventId: "event-1",
        isRead: false,
        createdAt: new Date()
      },
      {
        id: "notif-2",
        userId: "sample-user-1", 
        type: "announcement",
        title: "Announcement: Workshop Materials Available",
        message: "All workshop materials and setup instructions have been posted. Please check your email for the downloadable resources.",
        eventId: "event-1",
        isRead: false,
        createdAt: new Date()
      },
      {
        id: "notif-3",
        userId: "sample-user-1",
        type: "event_update",
        title: "Event Update: Contemporary Art Exhibition",
        message: "Special guest performance by Maria Santos added at 3 PM. Don't miss this exciting addition!",
        eventId: "event-2",
        isRead: true,
        createdAt: new Date()
      }
    ];

    sampleNotifications.forEach(notification => {
      this.notifications.set(notification.id, notification);
    });
  }

  // User methods
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async getUserByGoogleId(googleId: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.googleId === googleId);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { 
      ...insertUser, 
      avatar: insertUser.avatar || null,
      googleId: insertUser.googleId || null,
      id, 
      createdAt: new Date() 
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = { ...user, ...updates };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  async getEventsByOrganizer(organizerId: string): Promise<Event[]> {
    return Array.from(this.events.values()).filter(event => event.organizerId === organizerId);
  }

  // Event Categories
  async getEventCategories(): Promise<EventCategory[]> {
    return Array.from(this.eventCategories.values());
  }

  async getEventCategory(id: string): Promise<EventCategory | undefined> {
    return this.eventCategories.get(id);
  }

  // Events
  async getEvents(filters?: { 
    category?: string; 
    isPaid?: boolean; 
    status?: string; 
    hostId?: string;
    search?: string;
  }): Promise<Event[]> {
    let events = Array.from(this.events.values());

    if (filters?.category) {
      events = events.filter(event => event.categoryId === filters.category);
    }
    if (filters?.isPaid !== undefined) {
      events = events.filter(event => event.isPaid === filters.isPaid);
    }
    if (filters?.status) {
      events = events.filter(event => event.status === filters.status);
    }
    if (filters?.hostId) {
      events = events.filter(event => event.organizerId === filters.hostId);
    }
    if (filters?.search) {
      const searchLower = filters.search.toLowerCase();
      events = events.filter(event => {
        const matchesTitle = event.title.toLowerCase().includes(searchLower);
        const matchesDescription = event.description.toLowerCase().includes(searchLower);
        const matchesLocation = event.location.toLowerCase().includes(searchLower);
        const matchesTags = event.tags?.some(tag => tag.toLowerCase().includes(searchLower)) || false;

        return matchesTitle || matchesDescription || matchesLocation || matchesTags;
      });
    }

    // Sort by newest created first (createdAt), then by event date
    return events.sort((a, b) => {
      // First sort by creation date (newest first)
      const createdAtDiff = (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0);
      if (createdAtDiff !== 0) return createdAtDiff;
      // Then by event date (earliest first)
      return a.dateTime.getTime() - b.dateTime.getTime();
    });
  }

  async getEvent(id: string): Promise<Event | undefined> {
    return this.events.get(id);
  }

  async createEvent(insertEvent: InsertEvent): Promise<Event> {
    const id = randomUUID();
    const event: Event = { 
      ...insertEvent, 
      status: insertEvent.status || "upcoming",
      imageUrl: insertEvent.imageUrl || null,
      hostId: insertEvent.hostId || insertEvent.organizerId, // Ensure hostId is set
      id, 
      createdAt: new Date() 
    };
    this.events.set(id, event);
    return event;
  }

  async updateEvent(id: string, updates: Partial<Event>): Promise<Event | undefined> {
    const event = this.events.get(id);
    if (!event) return undefined;

    const updatedEvent = { ...event, ...updates };
    this.events.set(id, updatedEvent);
    return updatedEvent;
  }

  async deleteEvent(id: string): Promise<boolean> {
    return this.events.delete(id);
  }

  async getEventsByHost(hostId: string): Promise<Event[]> {
    return Array.from(this.events.values()).filter(event => event.organizerId === hostId);
  }

  async getMyHostedEvents(userId: string) {
    return Array.from(this.events.values())
      .filter(event => event.hostId === userId || event.organizerId === userId)
      .map(event => ({
        ...event,
        attendingCount: Array.from(this.rsvps.values()).filter(r => r.eventId === event.id && r.status === 'attending').length
      }))
      .sort((a, b) => {
        // Sort by newest created first (createdAt), then by event date
        const createdAtDiff = (b.createdAt?.getTime() || 0) - (a.createdAt?.getTime() || 0);
        if (createdAtDiff !== 0) return createdAtDiff;
        // Then by event date (earliest first)
        return a.dateTime.getTime() - b.dateTime.getTime();
      });
  }

  // RSVPs
  async getRsvpsByEvent(eventId: string): Promise<Rsvp[]> {
    return Array.from(this.rsvps.values()).filter(rsvp => rsvp.eventId === eventId);
  }

  async getRsvpsByUser(userId: string): Promise<Rsvp[]> {
    return Array.from(this.rsvps.values()).filter(rsvp => rsvp.userId === userId);
  }

  async getRsvp(eventId: string, userId: string): Promise<Rsvp | undefined> {
    return Array.from(this.rsvps.values()).find(
      rsvp => rsvp.eventId === eventId && rsvp.userId === userId
    );
  }

  async createRsvp(insertRsvp: InsertRsvp): Promise<Rsvp> {
    const id = randomUUID();
    const rsvp: Rsvp = { 
      ...insertRsvp, 
      id, 
      createdAt: new Date() 
    };
    this.rsvps.set(id, rsvp);
    return rsvp;
  }

  async updateRsvp(eventId: string, userId: string, status: string): Promise<Rsvp | undefined> {
    const rsvp = await this.getRsvp(eventId, userId);
    if (!rsvp) return undefined;

    const updatedRsvp = { ...rsvp, status };
    this.rsvps.set(rsvp.id, updatedRsvp);
    return updatedRsvp;
  }

  async deleteRsvp(eventId: string, userId: string): Promise<boolean> {
    const rsvp = await this.getRsvp(eventId, userId);
    if (!rsvp) return false;

    return this.rsvps.delete(rsvp.id);
  }

  async getMyRsvps(userId: string) {
    return Array.from(this.rsvps.values())
      .filter((rsvp) => rsvp.userId === userId)
      .map((rsvp) => {
        const event = Array.from(this.events.values()).find((event) => event.id === rsvp.eventId);
        return {
          ...rsvp,
          event: event ? {
            ...event,
            attendingCount: Array.from(this.rsvps.values()).filter(r => r.eventId === event.id && r.status === 'attending').length
          } : null,
        };
      })
      .filter((rsvp) => rsvp.event !== null);
  }

  // Reviews
  async getEventReviews(eventId: string): Promise<EventReview[]> {
    return Array.from(this.eventReviews.values()).filter(review => review.eventId === eventId);
  }

  async createEventReview(insertReview: InsertEventReview): Promise<EventReview> {
    const id = randomUUID();
    const review: EventReview = { 
      ...insertReview, 
      id, 
      createdAt: new Date() 
    };
    this.eventReviews.set(id, review);
    return review;
  }

  // Photos
  async getEventPhotos(eventId: string): Promise<EventPhoto[]> {
    return Array.from(this.eventPhotos.values()).filter(photo => photo.eventId === eventId);
  }

  async createEventPhoto(insertPhoto: InsertEventPhoto): Promise<EventPhoto> {
    const id = randomUUID();
    const photo: EventPhoto = { 
      ...insertPhoto, 
      id, 
      createdAt: new Date() 
    };
    this.eventPhotos.set(id, photo);
    return photo;
  }

  // Notifications
  async getNotifications(userId: string): Promise<Notification[]> {
    return Array.from(this.notifications.values())
      .filter(notification => notification.userId === userId)
      .sort((a, b) => (b.createdAt || new Date()).getTime() - (a.createdAt || new Date()).getTime());
  }

  async createNotification(insertNotification: InsertNotification): Promise<Notification> {
    const id = randomUUID();
    const notification: Notification = { 
      ...insertNotification, 
      id, 
      isRead: insertNotification.isRead || false,
      createdAt: new Date() 
    };
    this.notifications.set(id, notification);
    return notification;
  }

  async markNotificationRead(id: string): Promise<void> {
    const notification = this.notifications.get(id);
    if (notification) {
      this.notifications.set(id, { ...notification, isRead: true });
    }
  }

  // Announcements
  async getEventAnnouncements(eventId: string): Promise<Announcement[]> {
    return Array.from(this.announcements.values())
      .filter(announcement => announcement.eventId === eventId)
      .sort((a, b) => new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime());
  }

  async createAnnouncement(announcement: InsertAnnouncement): Promise<Announcement> {
    const newAnnouncement: Announcement = {
      ...announcement,
      id: randomUUID(),
      createdAt: new Date(),
    };
    this.announcements.set(newAnnouncement.id, newAnnouncement);
    return newAnnouncement;
  }

  // Favorites
  async getUserFavorites(userId: string): Promise<any[]> {
    const userFavorites = Array.from(this.favorites.values())
      .filter(favorite => favorite.userId === userId);

    // Include event data with each favorite
    return userFavorites.map(favorite => ({
      ...favorite,
      event: this.events.get(favorite.eventId)
    })).filter(fav => fav.event); // Only include favorites where event still exists
  }

  async createFavorite(favorite: InsertFavorite): Promise<Favorite> {
    const newFavorite: Favorite = {
      ...favorite,
      id: randomUUID(),
      createdAt: new Date(),
    };
    this.favorites.set(newFavorite.id, newFavorite);
    return newFavorite;
  }

  async deleteFavorite(userId: string, eventId: string): Promise<boolean> {
    const favorite = Array.from(this.favorites.values())
      .find(f => f.userId === userId && f.eventId === eventId);

    if (favorite) {
      this.favorites.delete(favorite.id);
      return true;
    }
    return false;
  }

  async isFavorited(userId: string, eventId: string): Promise<boolean> {
    return Array.from(this.favorites.values())
      .some(f => f.userId === userId && f.eventId === eventId);
  }
}

export const storage = new MemStorage();

