@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(20, 14.3%, 4.1%);
  --muted: hsl(60, 4.8%, 95.9%);
  --muted-foreground: hsl(25, 5.3%, 44.7%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(20, 14.3%, 4.1%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(20, 14.3%, 4.1%);
  --border: hsl(20, 5.9%, 90%);
  --input: hsl(20, 5.9%, 90%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(211, 100%, 99%);
  --secondary: hsl(60, 4.8%, 95.9%);
  --secondary-foreground: hsl(24, 9.8%, 10%);
  --accent: hsl(60, 4.8%, 95.9%);
  --accent-foreground: hsl(24, 9.8%, 10%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(60, 9.1%, 97.8%);
  --ring: hsl(20, 14.3%, 4.1%);
  --radius: 0.5rem;

  /* Custom EventHub colors */
  --event-primary: hsl(247, 84%, 63%);
  --event-secondary: hsl(151, 55%, 42%);
  --event-accent: hsl(37, 92%, 50%);
  --event-danger: hsl(0, 84%, 60%);

  /* Category colors */
  --category-music: hsl(220, 84%, 60%);
  --category-tech: hsl(151, 55%, 42%);
  --category-art: hsl(271, 65%, 60%);
  --category-sports: hsl(0, 84%, 60%);
  --category-food: hsl(37, 92%, 50%);
  --category-education: hsl(247, 84%, 63%);
}

.dark {
  --background: hsl(240, 15%, 7%);
  --foreground: hsl(0, 0%, 95%);
  --muted: hsl(240, 10%, 12%);
  --muted-foreground: hsl(240, 8%, 65%);
  --popover: hsl(240, 15%, 8%);
  --popover-foreground: hsl(0, 0%, 95%);
  --card: hsl(240, 12%, 10%);
  --card-foreground: hsl(0, 0%, 95%);
  --border: hsl(240, 8%, 18%);
  --input: hsl(240, 8%, 18%);
  --primary: hsl(207, 90%, 54%);
  --primary-foreground: hsl(240, 15%, 7%);
  --secondary: hsl(240, 8%, 18%);
  --secondary-foreground: hsl(0, 0%, 95%);
  --accent: hsl(240, 8%, 18%);
  --accent-foreground: hsl(0, 0%, 95%);
  --destructive: hsl(0, 84%, 60%);
  --destructive-foreground: hsl(0, 0%, 95%);
  --ring: hsl(207, 90%, 54%);
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

/* Tech Grid Background */
.bg-tech-grid {
  background-image: 
    linear-gradient(to right, rgba(59, 130, 246, 0.1) 1px, transparent 1px),
    linear-gradient(to bottom, rgba(59, 130, 246, 0.1) 1px, transparent 1px);
  background-size: 30px 30px;
}

/* Particle Animations */
.particle-1 {
  top: 20%;
  left: 10%;
}

.particle-2 {
  top: 60%;
  left: 80%;
}

.particle-3 {
  top: 80%;
  left: 20%;
}

.particle-4 {
  top: 30%;
  left: 70%;
}

.particle-5 {
  top: 70%;
  left: 50%;
}

/* Custom Animations */
@keyframes float-1 {
  0%, 100% { transform: translate(0, 0) rotate(0deg); }
  33% { transform: translate(30px, -30px) rotate(120deg); }
  66% { transform: translate(-20px, 20px) rotate(240deg); }
}

@keyframes float-2 {
  0%, 100% { transform: translate(0, 0) rotate(0deg); }
  50% { transform: translate(-40px, -40px) rotate(180deg); }
}

@keyframes float-3 {
  0%, 100% { transform: translate(0, 0) scale(1); }
  33% { transform: translate(20px, -40px) scale(1.2); }
  66% { transform: translate(-30px, 30px) scale(0.8); }
}

@keyframes spin-slow {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

@keyframes bounce-slow {
  0%, 100% { transform: translateY(0); }
  50% { transform: translateY(-20px); }
}

@keyframes pulse-slow {
  0%, 100% { opacity: 0.4; }
  50% { opacity: 0.8; }
}

.animate-spin-slow {
  animation: spin-slow 8s linear infinite;
}

/* Subtle glow effects */
.glow {
  box-shadow: 
    0 0 15px rgba(59, 130, 246, 0.3), 
    0 0 25px rgba(59, 130, 246, 0.2), 
    0 0 35px rgba(59, 130, 246, 0.1),
    inset 0 0 10px rgba(59, 130, 246, 0.1);
  animation: pulse-glow 3s ease-in-out infinite alternate;
}

.glow-purple {
  box-shadow: 
    0 0 15px rgba(139, 92, 246, 0.3), 
    0 0 25px rgba(139, 92, 246, 0.2), 
    0 0 35px rgba(139, 92, 246, 0.1),
    inset 0 0 10px rgba(139, 92, 246, 0.1);
  animation: pulse-glow-purple 3s ease-in-out infinite alternate;
}

.glow-green {
  box-shadow: 
    0 0 15px rgba(34, 197, 94, 0.3), 
    0 0 25px rgba(34, 197, 94, 0.2), 
    0 0 35px rgba(34, 197, 94, 0.1),
    inset 0 0 10px rgba(34, 197, 94, 0.1);
  animation: pulse-glow-green 3s ease-in-out infinite alternate;
}

@keyframes pulse-glow {
  0% {
    box-shadow: 
      0 0 15px rgba(59, 130, 246, 0.3), 
      0 0 25px rgba(59, 130, 246, 0.2), 
      0 0 35px rgba(59, 130, 246, 0.1),
      inset 0 0 10px rgba(59, 130, 246, 0.1);
  }
  100% {
    box-shadow: 
      0 0 20px rgba(59, 130, 246, 0.4), 
      0 0 35px rgba(59, 130, 246, 0.3), 
      0 0 50px rgba(59, 130, 246, 0.2),
      inset 0 0 15px rgba(59, 130, 246, 0.15);
  }
}

@keyframes pulse-glow-purple {
  0% {
    box-shadow: 
      0 0 15px rgba(139, 92, 246, 0.3), 
      0 0 25px rgba(139, 92, 246, 0.2), 
      0 0 35px rgba(139, 92, 246, 0.1),
      inset 0 0 10px rgba(139, 92, 246, 0.1);
  }
  100% {
    box-shadow: 
      0 0 20px rgba(139, 92, 246, 0.4), 
      0 0 35px rgba(139, 92, 246, 0.3), 
      0 0 50px rgba(139, 92, 246, 0.2),
      inset 0 0 15px rgba(139, 92, 246, 0.15);
  }
}

@keyframes pulse-glow-green {
  0% {
    box-shadow: 
      0 0 15px rgba(34, 197, 94, 0.3), 
      0 0 25px rgba(34, 197, 94, 0.2), 
      0 0 35px rgba(34, 197, 94, 0.1),
      inset 0 0 10px rgba(34, 197, 94, 0.1);
  }
  100% {
    box-shadow: 
      0 0 20px rgba(34, 197, 94, 0.4), 
      0 0 35px rgba(34, 197, 94, 0.3), 
      0 0 50px rgba(34, 197, 94, 0.2),
      inset 0 0 15px rgba(34, 197, 94, 0.15);
  }
}

.animate-bounce-slow {
  animation: bounce-slow 3s ease-in-out infinite;
}

.animate-pulse-slow {
  animation: pulse-slow 4s ease-in-out infinite;
}

@keyframes float-4 {
  0%, 100% { transform: translate(0, 0) rotate(0deg); }
  25% { transform: translate(25px, -15px) rotate(90deg); }
  50% { transform: translate(-15px, -25px) rotate(180deg); }
  75% { transform: translate(-25px, 15px) rotate(270deg); }
}

@keyframes float-5 {
  0%, 100% { transform: translate(0, 0) scale(1) rotate(0deg); }
  50% { transform: translate(35px, -20px) scale(1.3) rotate(180deg); }
}

@keyframes circuit-flow {
  0% { stroke-dashoffset: 0; }
  100% { stroke-dashoffset: 100; }
}

@keyframes circuit-flow-reverse {
  0% { stroke-dashoffset: 100; }
  100% { stroke-dashoffset: 0; }
}

@keyframes glow-text {
  0%, 100% { text-shadow: 0 0 20px rgba(59, 130, 246, 0.5); }
  50% { text-shadow: 0 0 30px rgba(59, 130, 246, 0.8), 0 0 40px rgba(139, 92, 246, 0.3); }
}

@keyframes gradient-shift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.animate-glow-text {
  animation: glow-text 3s ease-in-out infinite;
}

.animate-gradient-shift {
  background-size: 200% 200%;
  animation: gradient-shift 4s ease-in-out infinite;
}

.animate-float-1 {
  animation: float-1 6s ease-in-out infinite;
}

.animate-float-2 {
  animation: float-2 8s ease-in-out infinite;
}

.animate-float-3 {
  animation: float-3 10s ease-in-out infinite;
}

.animate-float-4 {
  animation: float-4 7s ease-in-out infinite;
}

.animate-float-5 {
  animation: float-5 9s ease-in-out infinite;
}

.animate-circuit-flow {
  stroke-dasharray: 5 5;
  animation: circuit-flow 3s linear infinite;
}

.animate-circuit-flow-reverse {
  stroke-dasharray: 5 5;
  animation: circuit-flow-reverse 3s linear infinite;
}

/* Electric Cloud animation effects */
@keyframes electric-cloud-drift {
  0% { 
    transform: translateX(-20px) translateY(0px) scale(1);
    opacity: 0.3;
  }
  25% { 
    transform: translateX(10px) translateY(-15px) scale(1.1);
    opacity: 0.6;
  }
  50% { 
    transform: translateX(30px) translateY(5px) scale(0.9);
    opacity: 0.8;
  }
  75% { 
    transform: translateX(15px) translateY(-10px) scale(1.05);
    opacity: 0.5;
  }
  100% { 
    transform: translateX(-20px) translateY(0px) scale(1);
    opacity: 0.3;
  }
}

@keyframes electric-cloud-glow {
  0%, 100% { 
    filter: blur(20px) brightness(1);
    box-shadow: 
      0 0 30px rgba(59, 130, 246, 0.3),
      0 0 60px rgba(139, 92, 246, 0.2),
      0 0 90px rgba(34, 197, 94, 0.1);
  }
  50% { 
    filter: blur(25px) brightness(1.3);
    box-shadow: 
      0 0 50px rgba(59, 130, 246, 0.5),
      0 0 100px rgba(139, 92, 246, 0.4),
      0 0 150px rgba(34, 197, 94, 0.3);
  }
}

@keyframes electric-cloud-morph {
  0% { 
    border-radius: 50% 40% 60% 30%;
    transform: rotate(0deg);
  }
  25% { 
    border-radius: 40% 60% 30% 50%;
    transform: rotate(90deg);
  }
  50% { 
    border-radius: 60% 30% 50% 40%;
    transform: rotate(180deg);
  }
  75% { 
    border-radius: 30% 50% 40% 60%;
    transform: rotate(270deg);
  }
  100% { 
    border-radius: 50% 40% 60% 30%;
    transform: rotate(360deg);
  }
}

.animate-electric-cloud-1 {
  animation: electric-cloud-drift 8s ease-in-out infinite, 
             electric-cloud-glow 4s ease-in-out infinite,
             electric-cloud-morph 12s linear infinite;
}

.animate-electric-cloud-2 {
  animation: electric-cloud-drift 10s ease-in-out infinite reverse, 
             electric-cloud-glow 5s ease-in-out infinite,
             electric-cloud-morph 15s linear infinite reverse;
}

.animate-electric-cloud-3 {
  animation: electric-cloud-drift 12s ease-in-out infinite, 
             electric-cloud-glow 6s ease-in-out infinite,
             electric-cloud-morph 18s linear infinite;
}

/* Cursor Glow Effect */
.cursor-glow {
  position: relative;
  cursor: none;
}

.cursor-glow::before {
  content: '';
  position: fixed;
  top: var(--mouse-y, 50%);
  left: var(--mouse-x, 50%);
  width: 300px;
  height: 300px;
  background: radial-gradient(circle, rgba(59, 130, 246, 0.1) 0%, rgba(139, 92, 246, 0.05) 40%, transparent 70%);
  border-radius: 50%;
  pointer-events: none;
  z-index: 5;
  transform: translate(-50%, -50%);
  transition: opacity 0.2s ease;
  opacity: 0.6;
}

@media (hover: hover) {
  .cursor-glow {
    cursor: default;
  }

  .cursor-glow::before {
    opacity: 0.8;
  }
}

/* Better button hover effects */
.btn-glow {
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(59, 130, 246, 0.2);
}

.btn-glow:hover {
  box-shadow: 0 8px 25px rgba(59, 130, 246, 0.4);
  transform: translateY(-2px);
}

@keyframes glow-text {
  0%, 100% { text-shadow: 0 0 20px rgba(59, 130, 246, 0.5); }
  50% { text-shadow: 0 0 40px rgba(59, 130, 246, 0.8), 0 0 60px rgba(139, 92, 246, 0.3); }
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

@keyframes pulse-slow {
  0%, 100% { opacity: 0.3; }
  50% { opacity: 0.6; }
}

/* Animation Classes */
.animate-float-1 { animation: float-1 8s ease-in-out infinite; }
.animate-float-2 { animation: float-2 6s ease-in-out infinite; }
.animate-float-3 { animation: float-3 10s ease-in-out infinite; }
.animate-float-4 { animation: float-4 7s ease-in-out infinite; }
.animate-float-5 { animation: float-5 9s ease-in-out infinite; }
.animate-circuit-flow { 
  stroke-dasharray: 20 10; 
  animation: circuit-flow 3s linear infinite; 
}
.animate-circuit-flow-reverse { 
  stroke-dasharray: 15 8; 
  animation: circuit-flow-reverse 4s linear infinite; 
}
.animate-glow-text { animation: glow-text 2s ease-in-out infinite; }
.animate-gradient-shift { 
  background-size: 400% 400%; 
  animation: gradient-shift 3s ease infinite; 
}
.animate-pulse-slow { animation: pulse-slow 4s ease-in-out infinite; }

/* Smooth scrolling */
html {
  scroll-behavior: smooth;
}

@layer components {
  .event-card-hover {
    @apply hover:shadow-xl hover:-translate-y-1 transition-all duration-300;
  }

  .category-icon {
    @apply w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 group-hover:scale-110 transition-transform duration-300;
  }

  .gradient-hero {
    background: linear-gradient(135deg, var(--event-primary), var(--event-accent));
  }

  .glassmorphism {
    @apply bg-white/10 backdrop-blur-md border border-white/20;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  @apply bg-gray-100 dark:bg-gray-800;
}

::-webkit-scrollbar-thumb {
  @apply bg-gray-300 dark:bg-gray-600 rounded;
}

::-webkit-scrollbar-thumb:hover {
  @apply bg-gray-400 dark:bg-gray-500;
}

/* Animation for floating action button */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

.float-animation {
  animation: float 3s ease-in-out infinite;
}

/* Star rating styles */
.star-rating {
  @apply flex items-center space-x-1;
}

.star-rating .star {
  @apply text-gray-300 dark:text-gray-600 cursor-pointer transition-colors duration-200;
}

.star-rating .star.filled {
  @apply text-yellow-400;
}

.star-rating .star:hover {
  @apply text-yellow-400;
}

/* Loading skeleton styles */
.skeleton {
  @apply animate-pulse bg-gray-200 dark:bg-gray-700 rounded;
}

/* Event status badges */
.status-upcoming {
  @apply bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300;
}

.status-ongoing {
  @apply bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300;
}

.status-completed {
  @apply bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300;
}

.status-cancelled {
  @apply bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300;
}

