import { google } from 'googleapis';

// Get the correct redirect URI based on the current environment
const getRedirectUri = () => {
  if (process.env.NODE_ENV === 'production' && process.env.REPLIT_DOMAIN) {
    return `https://${process.env.REPLIT_DOMAIN}/api/calendar/callback`;
  }
  // For development or when domain is not available, use the current request's host
  return process.env.REPLIT_DOMAIN 
    ? `https://${process.env.REPLIT_DOMAIN}/api/calendar/callback`
    : 'https://182dd87c-465c-4a94-9c83-04176f5770d9-00-1vxph47kk5vln.sisko.replit.dev/api/calendar/callback';
};

const oauth2Client = new google.auth.OAuth2(
  process.env.GOOGLE_CLIENT_ID,
  process.env.GOOGLE_CLIENT_SECRET,
  getRedirectUri()
);

export async function createCalendarEvent(accessToken: string, event: any) {
  try {
    oauth2Client.setCredentials({ access_token: accessToken });

    const calendar = google.calendar({ version: 'v3', auth: oauth2Client });

    const calendarEvent = {
      summary: event.title,
      description: event.description,
      location: event.location,
      start: {
        dateTime: event.dateTime,
        timeZone: 'America/New_York', // You can make this dynamic
      },
      end: {
        dateTime: new Date(new Date(event.dateTime).getTime() + 2 * 60 * 60 * 1000).toISOString(), // Add 2 hours
        timeZone: 'America/New_York',
      },
      attendees: event.attendees || [],
      reminders: {
        useDefault: false,
        overrides: [
          { method: 'email', minutes: 24 * 60 }, // 1 day before
          { method: 'popup', minutes: 60 }, // 1 hour before
        ],
      },
    };

    const response = await calendar.events.insert({
      calendarId: 'primary',
      requestBody: calendarEvent,
    });

    return response.data;
  } catch (error) {
    console.error('Error creating calendar event:', error);
    throw error;
  }
}

export async function getCalendarAuthUrl(userId: string) {
  const scopes = [
    'https://www.googleapis.com/auth/calendar',
    'https://www.googleapis.com/auth/calendar.events'
  ];

  const authUrl = oauth2Client.generateAuthUrl({
    access_type: 'offline',
    scope: scopes,
    state: userId, // Pass user ID to identify after callback
  });

  return authUrl;
}

export async function exchangeCodeForTokens(code: string) {
  try {
    const { tokens } = await oauth2Client.getToken(code);
    return tokens;
  } catch (error) {
    console.error('Error exchanging code for tokens:', error);
    throw error;
  }
}

server/notifications.ts.content:
import { storage } from "./storage";

export interface NotificationData {
  id: string;
  userId: string;
  title: string;
  message: string;
  type: 'event_reminder' | 'event_update' | 'event_cancelled' | 'new_rsvp' | 'calendar_sync';
  eventId?: string;
  isRead: boolean;
  createdAt: Date;
}

export class NotificationManager {
  private notifications: Map<string, NotificationData[]> = new Map();

  async createNotification(notification: Omit<NotificationData, 'id' | 'createdAt' | 'isRead'>) {
    const id = `notif-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
    const newNotification: NotificationData = {
      ...notification,
      id,
      isRead: false,
      createdAt: new Date(),
    };

    const userNotifications = this.notifications.get(notification.userId) || [];
    userNotifications.unshift(newNotification);
    this.notifications.set(notification.userId, userNotifications);

    return newNotification;
  }

  async getUserNotifications(userId: string): Promise<NotificationData[]> {
    return this.notifications.get(userId) || [];
  }

  async markAsRead(userId: string, notificationId: string): Promise<boolean> {
    const userNotifications = this.notifications.get(userId);
    if (!userNotifications) return false;

    const notification = userNotifications.find(n => n.id === notificationId);
    if (notification) {
      notification.isRead = true;
      return true;
    }
    return false;
  }

  async markAllAsRead(userId: string): Promise<boolean> {
    const userNotifications = this.notifications.get(userId);
    if (!userNotifications) return false;

    userNotifications.forEach(notification => {
      notification.isRead = true;
    });
    return true;
  }

  async deleteNotification(userId: string, notificationId: string): Promise<boolean> {
    const userNotifications = this.notifications.get(userId);
    if (!userNotifications) return false;

    const index = userNotifications.findIndex(n => n.id === notificationId);
    if (index !== -1) {
      userNotifications.splice(index, 1);
      return true;
    }
    return false;
  }

  // Notification generators for specific events
  async notifyEventUpdate(eventId: string, message: string) {
    try {
      const rsvps = await storage.getRsvpsByEvent(eventId);
      const event = await storage.getEvent(eventId);

      if (!event) return;

      for (const rsvp of rsvps) {
        if (rsvp.status === 'attending') {
          await this.createNotification({
            userId: rsvp.userId,
            title: 'Event Update',
            message: `${event.title}: ${message}`,
            type: 'event_update',
            eventId,
          });
        }
      }
    } catch (error) {
      console.error('Error sending event update notifications:', error);
    }
  }

  async notifyNewRSVP(eventId: string, userName: string) {
    try {
      const event = await storage.getEvent(eventId);
      if (!event) return;

      await this.createNotification({
        userId: event.hostId,
        title: 'New RSVP',
        message: `${userName} has RSVP'd to your event "${event.title}"`,
        type: 'new_rsvp',
        eventId,
      });
    } catch (error) {
      console.error('Error sending new RSVP notification:', error);
    }
  }

  async notifyEventReminder(eventId: string) {
    try {
      const rsvps = await storage.getRsvpsByEvent(eventId);
      const event = await storage.getEvent(eventId);

      if (!event) return;

      const eventDate = new Date(event.dateTime);
      const now = new Date();
      const tomorrow = new Date();
      tomorrow.setDate(tomorrow.getDate() + 1);
      const oneHourFromNow = new Date(now.getTime() + 60 * 60 * 1000);

      // Send reminders for events happening tomorrow (1-day reminder)
      if (eventDate.toDateString() === tomorrow.toDateString()) {
        for (const rsvp of rsvps) {
          if (rsvp.status === 'attending') {
            await this.createNotification({
              userId: rsvp.userId,
              title: 'Event Reminder - Tomorrow',
              message: `Don't forget: "${event.title}" is tomorrow at ${eventDate.toLocaleTimeString()}`,
              type: 'event_reminder',
              eventId,
            });
          }
        }
      }

      // Send reminders for events happening within the next hour (1-hour reminder)
      if (eventDate > now && eventDate <= oneHourFromNow) {
        for (const rsvp of rsvps) {
          if (rsvp.status === 'attending') {
            await this.createNotification({
              userId: rsvp.userId,
              title: 'Event Starting Soon!',
              message: `"${event.title}" starts in less than an hour at ${eventDate.toLocaleTimeString()}. Don't miss it!`,
              type: 'event_reminder',
              eventId,
            });
          }
        }
      }
    } catch (error) {
      console.error('Error sending event reminders:', error);
    }
  }

  async notifyCalendarSync(userId: string, success: boolean, eventTitle: string) {
    await this.createNotification({
      userId,
      title: success ? 'Calendar Synced' : 'Calendar Sync Failed',
      message: success 
        ? `"${eventTitle}" has been added to your Google Calendar`
        : `Failed to sync "${eventTitle}" to your Google Calendar. Please try again.`,
      type: 'calendar_sync',
    });
  }
}

export const notificationManager = new NotificationManager();

// Background job to send daily reminders (in a real app, you'd use a cron job)
setInterval(async () => {
  try {
    const events = await storage.getEvents({});
    for (const event of events) {
      await notificationManager.notifyEventReminder(event.id);
    }
  } catch (error) {
    console.error('Error in daily reminder job:', error);
  }
}, 24 * 60 * 60 * 1000); // Run daily

// Background job to send hourly reminders for events starting soon
setInterval(async () => {
  try {
    const events = await storage.getEvents({});
    for (const event of events) {
      await notificationManager.notifyEventReminder(event.id);
    }
  } catch (error) {
    console.error('Error in hourly reminder job:', error);
  }
}, 60 * 60 * 1000); // Run every hour

